import React from 'react';

function Login({ setToken }) {
    const handleSubmit = async (e) => {
        e.preventDefault();

        const username = e.target.username.value;
        const password = e.target.password.value;

        try {
            // Update the URL to the correct endpoint provided by your backend
            const response = await fetch('http://localhost:3333/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (!response.ok) {
                throw new Error(`HTTP status ${response.status}`);
            }

            const data = await response.json();
            // Assuming the backend sends back a token, and it's stored in a property called 'token'
            setToken(data.token);

            // You might want to redirect the user to another page at this point
        } catch (error) {
            console.error('Login failed:', error);
            // You should handle the error by displaying a message to the user
        }
    }

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor='username'>Username:</label>
                <input id='username' name='username' type='text' required />

                <label htmlFor='password'>Password:</label>
                <input id='password' name='password' type='password' required />

                <button type='submit'>Login</button>
            </form>
        </div>
    );
}

export default Login;
